INSERT INTO `content` (`con_id`, `con_title`, `con_intro`, `con_image`, `con_detail`, `con_tags`, `con_createdate`, `con_editdate`, `con_type`, `con_catid`, `con_view`, `con_interesting`, `con_vote`, `con_publish`, `con_user`) VALUES
(1, 'Clip: Bạn sẽ bất ngờ vì những điều giản dị bố thật sự ước mong được dành tặng', '&lt;h4 class=&quot;knd-sapo&quot; data-field=&quot;sapo&quot;&gt;C&amp;oacute; &quot;Ng&amp;agrave;y của Mẹ&quot;, tất nhi&amp;ecirc;n l&amp;agrave; sẽ c&amp;oacute; &quot;Ng&amp;agrave;y của Bố&quot;, nhưng dường như ch&amp;uacute;ng ta thường chỉ để &am', 'ngay-cua-cha-14092.jpg', '&lt;h3 class=&quot;knd-sapo&quot; data-field=&quot;sapo&quot;&gt;C&amp;oacute; &quot;Ng&amp;agrave;y của Mẹ&quot;, tất nhi&amp;ecirc;n l&amp;agrave; sẽ c&amp;oacute; &quot;Ng&amp;agrave;y của Bố&quot;, nhưng dường như ch&amp;uacute;ng ta thường chỉ để &amp;yacute; đến việc bộc lộ t&amp;igrave;nh cảm với mẹ m&amp;agrave; qu&amp;ecirc;n đi việc n&amp;oacute;i lời y&amp;ecirc;u thương với bố, v&amp;igrave; trong ấn tượng của ch&amp;uacute;ng ta, bố đ&amp;oacute;ng vai một c&amp;acirc;y đại thụ che trời, sừng sững m&amp;agrave; x&amp;ugrave; x&amp;igrave;, kh&amp;ocirc; khan v&amp;agrave; lu&amp;ocirc;n mạnh mẽ.&amp;nbsp;&lt;/h3&gt;\r\n&lt;div class=&quot;knd-content&quot; data-field=&quot;body&quot;&gt;\r\n&lt;p&gt;&quot;B&amp;aacute;c đ&amp;atilde; nhận được m&amp;oacute;n qu&amp;agrave; g&amp;igrave; v&amp;agrave;o Ng&amp;agrave;y của Bố?&quot; &quot;Con t&amp;ocirc;i chẳng tặng g&amp;igrave; đ&amp;acirc;u.&quot; &quot;Ch&amp;uacute;ng n&amp;oacute; chỉ tặng mẹ ch&amp;uacute;ng n&amp;oacute; th&amp;ocirc;i, c&amp;oacute; tặng g&amp;igrave; t&amp;ocirc;i đ&amp;acirc;u.&quot; &quot;Chỉ cần l&amp;agrave; con t&amp;ocirc;i tặng, c&amp;aacute;i g&amp;igrave; t&amp;ocirc;i cũng th&amp;iacute;ch.&quot;&lt;/p&gt;\r\n&lt;p&gt;&quot;B&amp;aacute;c c&amp;oacute; muốn n&amp;oacute;i g&amp;igrave; với c&amp;aacute;c con b&amp;aacute;c kh&amp;ocirc;ng?&quot; &quot;Giữ g&amp;igrave;n sức khỏe.&quot; &quot;Chăm chỉ l&amp;agrave;m việc.&quot; &quot;Về thăm nh&amp;agrave; nhiều hơn&quot;&lt;/p&gt;\r\n&lt;p&gt;V&amp;agrave; c&amp;ograve;n rất nhiều c&amp;acirc;u hỏi, c&amp;acirc;u trả lời kh&amp;aacute;c trong clip thực tế dưới đ&amp;acirc;y cho ch&amp;uacute;ng ta thấy trong mắt những &amp;ocirc;ng bố Ng&amp;agrave;y d&amp;agrave;nh cho bản th&amp;acirc;n l&amp;agrave; ng&amp;agrave;y như thế n&amp;agrave;o.&lt;/p&gt;\r\n&lt;p&gt;Ch&amp;uacute;ng ta thường rất t&amp;igrave;nh cảm với mẹ, ch&amp;uacute;ng ta c&amp;oacute; thể dễ d&amp;agrave;ng n&amp;oacute;i ra những c&amp;acirc;u y&amp;ecirc;u mẹ, ngay cả Ng&amp;agrave;y của Mẹ ch&amp;uacute;ng ta cũng chuẩn bị rất kĩ những lời ch&amp;uacute;c, những m&amp;oacute;n qu&amp;agrave;, nhưng dường như kh&amp;ocirc;ng mấy người trong ch&amp;uacute;ng ta nhớ được Ng&amp;agrave;y của Bố l&amp;agrave; ng&amp;agrave;y n&amp;agrave;o, chứ đừng n&amp;oacute;i đến việc ch&amp;uacute;c mừng bố. C&amp;oacute; lẽ bởi trong ấn tượng của mỗi đứa con, người bố lu&amp;ocirc;n đ&amp;oacute;ng vai tr&amp;ograve; trầm lặng, nghi&amp;ecirc;m khắc, một b&amp;oacute;ng d&amp;aacute;ng cao lớn, x&amp;ugrave; x&amp;igrave;, vậy n&amp;ecirc;n việc bộc lộ t&amp;igrave;nh cảm với bố cũng trở n&amp;ecirc;n kh&amp;oacute; khăn hơn.&lt;/p&gt;\r\n&lt;p&gt;Xem xong clip, bạn sẽ thấy khi được hỏi về Ng&amp;agrave;y của Bố, kh&amp;ocirc;ng &amp;iacute;t người bố đ&amp;atilde; phải thốt l&amp;ecirc;n c&amp;acirc;u: &quot;Ng&amp;agrave;y của Bố &amp;agrave;? L&amp;agrave; ng&amp;agrave;y n&amp;agrave;o vậy? T&amp;ocirc;i kh&amp;ocirc;ng biết&quot;, bạn sẽ thấy được những n&amp;eacute;t buồn khi được hỏi con c&amp;aacute;i c&amp;oacute; tặng g&amp;igrave; họ nh&amp;acirc;n Ng&amp;agrave;y của Bố kh&amp;ocirc;ng v&amp;agrave; cả những y&amp;ecirc;u thương khi mong muốn c&amp;aacute;c con b&amp;igrave;nh an, mạnh khỏe. Ước muốn của những người cha ấy thật đơn giản, chỉ l&amp;agrave; một bữa cơm, một lần về thăm nh&amp;agrave;, một cuộc điện thoại... Vậy m&amp;agrave; nhiều người con vẫn lấy c&amp;aacute;i cớ &quot;bận rộn&quot; để khước từ n&amp;oacute;.&lt;/p&gt;\r\n&lt;div&gt;C&amp;aacute;c bạn ạ, Ng&amp;agrave;y của Bố cũng kh&amp;ocirc;ng c&amp;ograve;n xa nữa, h&amp;atilde;y đừng để n&amp;oacute; tr&amp;ocirc;i qua như bao ng&amp;agrave;y kh&amp;aacute;c nh&amp;eacute;, bộc lộ t&amp;igrave;nh y&amp;ecirc;u với những người thương bạn nhất kh&amp;ocirc;ng c&amp;oacute; g&amp;igrave; đ&amp;aacute;ng để ngượng ng&amp;ugrave;ng cả! H&amp;atilde;y y&amp;ecirc;u thương khi chưa qu&amp;aacute; muộn!&lt;/div&gt;\r\n&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: right;&quot;&gt;&lt;strong&gt;Nguồn: kenh14.vn&lt;/strong&gt;&lt;/div&gt;\r\n&lt;/div&gt;', 'kenh14, dailyaz, túi ví, giày da, bóp da', '2018-03-10 07:57:39', '2018-03-24 08:06:38', 1, 7, 1, 0, 0, 1, 1),
(2, 'Tìm hiểu nguồn gốc và ý nghĩa Ngày của Cha', '&lt;p&gt;&lt;strong&gt;Ch&amp;uacute;ng ta thường hay l&amp;atilde;ng qu&amp;ecirc;n về sự hy sinh cao cả của người cha cũng như vai tr&amp;ograve; của cha trong cuộc sống của m&amp;igrave;nh. Ng&amp;agrave;y của Cha ch&amp;iacute;nh l&amp;agrave; cơ hội ', '18zal86ios4wgjpg.jpg', '&lt;p&gt;&lt;strong&gt;Ch&amp;uacute;ng ta thường hay l&amp;atilde;ng qu&amp;ecirc;n về sự hy sinh cao cả của người cha cũng như vai tr&amp;ograve; của cha trong cuộc sống của m&amp;igrave;nh. Ng&amp;agrave;y của Cha ch&amp;iacute;nh l&amp;agrave; cơ hội để ch&amp;uacute;ng ta chuộc lại sự l&amp;atilde;ng qu&amp;ecirc;n đ&amp;oacute;.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;h2&gt;Nguồn gốc v&amp;agrave; &amp;yacute; nghĩa Ng&amp;agrave;y của Cha&lt;/h2&gt;\r\n&lt;p&gt;Trong số những ng&amp;agrave;y lễ trong năm, &amp;iacute;t ai biết tới một ng&amp;agrave;y lễ d&amp;agrave;nh cho người cha thương y&amp;ecirc;u của m&amp;igrave;nh.&amp;nbsp;Ng&amp;agrave;y của Cha năm nay (21/6) l&amp;agrave; dịp để bạn tỏ l&amp;ograve;ng thương y&amp;ecirc;u, k&amp;iacute;nh trọng tới người sinh th&amp;agrave;nh, nu&amp;ocirc;i lớn m&amp;igrave;nh.&lt;/p&gt;\r\n&lt;p&gt;Ng&amp;agrave;y của cha l&amp;agrave; một dịp t&amp;ocirc;n vinh những người l&amp;agrave;m cha, cương vị l&amp;agrave;m cha, mối quan hệ với cha v&amp;agrave; ảnh hưởng của người cha trong x&amp;atilde; hội. Ng&amp;agrave;y n&amp;agrave;y được tổ chức v&amp;agrave;o&amp;nbsp;Chủ nhật thứ 3 của th&amp;aacute;ng 6 tại nhiều quốc gia v&amp;agrave; c&amp;oacute; thể rơi v&amp;agrave;o những ng&amp;agrave;y kh&amp;aacute;c ở một số nơi.&lt;/p&gt;\r\n&lt;h3&gt;Nguồn gốc Ng&amp;agrave;y của Cha&lt;/h3&gt;\r\n&lt;p&gt;Ng&amp;agrave;y của Cha được ăn mừng phổ biến từ những năm đầu thế kỷ 20 để bổ sung cho Ng&amp;agrave;y của Mẹ trong những ng&amp;agrave;y lễ t&amp;ocirc;n vinh c&amp;aacute;c bậc l&amp;agrave;m cha l&amp;agrave;m mẹ. Ng&amp;agrave;y của Cha được cử h&amp;agrave;nh v&amp;agrave;o nhiều ng&amp;agrave;y kh&amp;aacute;c nhau tr&amp;ecirc;n to&amp;agrave;n thế giới v&amp;agrave; thường li&amp;ecirc;n quan đến việc tặng qu&amp;agrave;, bữa tối đặc biệt cho ca, mẹ v&amp;agrave; c&amp;aacute;c hoạt động gia đ&amp;igrave;nh.&lt;/p&gt;\r\n&lt;p&gt;Nhưng việc ăn mừng Ng&amp;agrave;y của Cha được biết đến sớm nhất diễn ra ở Fairmont,T&amp;acirc;y Virginia v&amp;agrave;o ng&amp;agrave;y 5 th&amp;aacute;ng 7&amp;nbsp;năm 1908. Sự kiện được b&amp;agrave; Grace Golden Clayton tổ chức, với mong muốn vinh danh cuộc đời của người cha bị mất v&amp;agrave;i th&amp;aacute;ng trước trong&amp;nbsp;Thảm họa Monongah Mining ở Monongah, T&amp;acirc;y Virginia, v&amp;agrave;o ng&amp;agrave;y 06/12/1907. C&amp;oacute; thể Clayton chịu ảnh hưởng bởi việc ăn mừng Ng&amp;agrave;y của Mẹ lần đầu ti&amp;ecirc;n trong năm đ&amp;oacute;, Clayton đ&amp;atilde; chọn&amp;nbsp;ng&amp;agrave;y Chủ nhật gần nhất so với ng&amp;agrave;y sinh của người cha vừa mới qua đời của b&amp;agrave;.&lt;/p&gt;\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, sự kiện đ&amp;oacute; bị lu mờ bởi c&amp;aacute;c sự kiện kh&amp;aacute;c trong th&amp;agrave;nh phố. Tiểu bang T&amp;acirc;y Virginia kh&amp;ocirc;ng ch&amp;iacute;nh thức c&amp;ocirc;ng nhận ng&amp;agrave;y lễ n&amp;agrave;y, v&amp;agrave; n&amp;oacute; kh&amp;ocirc;ng được tổ chức trở lại. Tất cả c&amp;ocirc;ng lao trong việc gi&amp;uacute;p Ng&amp;agrave;y của Cha ra đời về sau lại được ghi nhận cho&amp;nbsp;Sonora Dodd người Spokane, đ&amp;atilde; tổ chức Ng&amp;agrave;y của Cha một c&amp;aacute;ch độc lập v&amp;agrave;o 2 năm sau đ&amp;oacute;. Sự kiện của b&amp;agrave; cũng chịu ảnh hưởng từ Ng&amp;agrave;y của Mẹ.&lt;/p&gt;\r\n&lt;p&gt;V&amp;agrave;o một ng&amp;agrave;y trong năm 1909, trong khi đang nghe b&amp;agrave;i thuyết gi&amp;aacute;o về Ng&amp;agrave;y của Mẹ, c&amp;ocirc; Sonora đ&amp;atilde; nghĩ đến một ng&amp;agrave;y để vinh danh c&amp;aacute;c người cha. Sonora l&amp;agrave; con g&amp;aacute;i lớn nhất trong s&amp;aacute;u chị em, mẹ c&amp;ocirc; qua đời trong l&amp;uacute;c sinh n&amp;ecirc;n cha c&amp;ocirc; l&amp;agrave; &amp;ocirc;ng William Jackson Smart đ&amp;atilde; một m&amp;igrave;nh nu&amp;ocirc;i s&amp;aacute;u chị em kh&amp;ocirc;n lớn. Sonora y&amp;ecirc;u qu&amp;yacute; v&amp;agrave; k&amp;iacute;nh trọng cha v&amp;igrave; hiểu những nỗi vất vả của &amp;ocirc;ng. Người cha trong mắt c&amp;ocirc; l&amp;agrave; biểu tượng của sự&amp;nbsp;hy sinh, vị tha, bao dung. V&amp;igrave; vậy, c&amp;ocirc; muốn c&amp;oacute; một ng&amp;agrave;y đặc biệt để t&amp;ocirc;n vinh cha m&amp;igrave;nh. Sonora đ&amp;atilde; chọn ng&amp;agrave;y 19 th&amp;aacute;ng 6 l&amp;agrave;&amp;nbsp;&lt;em&gt;&amp;ldquo;Ng&amp;agrave;y của Cha&amp;rdquo;&lt;/em&gt;v&amp;igrave; ng&amp;agrave;y đ&amp;oacute; l&amp;agrave; sinh nhật của cha c&amp;ocirc;.&lt;/p&gt;\r\n&lt;p&gt;Sau đ&amp;oacute;, v&amp;agrave;o năm 1966,&amp;nbsp;tổng thống Mỹ Lyndon Johnson đ&amp;atilde; quyết định ch&amp;iacute;nh chức chọn ng&amp;agrave;y Chủ nhật thứ 3 trong th&amp;aacute;ng 6 h&amp;agrave;ng năm l&amp;agrave; Ng&amp;agrave;y của Cha tr&amp;ecirc;n to&amp;agrave;n nước Mỹ. Ng&amp;agrave;y của Cha đ&amp;atilde; được tổ chức kỷ niệm h&amp;agrave;ng năm kể từ khi tổng thống Mỹ Richard Nixon k&amp;yacute; luật c&amp;ocirc;ng nhận Ng&amp;agrave;y của Cha c&amp;oacute; gi&amp;aacute; trị l&amp;agrave; một&amp;nbsp;ng&amp;agrave;y lễ ch&amp;iacute;nh thống vĩnh viễn v&amp;agrave;o năm 1972.&lt;/p&gt;\r\n&lt;h3&gt;&amp;Yacute; nghĩa Ng&amp;agrave;y của Cha&lt;/h3&gt;\r\n&lt;p&gt;Ng&amp;agrave;y lễ tri &amp;acirc;n người th&amp;acirc;n trong gia đ&amp;igrave;nh như Ng&amp;agrave;y của Cha l&amp;agrave; một&amp;nbsp;m&amp;oacute;n qu&amp;agrave; &amp;yacute; nghĩa, chứa đựng tấm l&amp;ograve;ng của con c&amp;aacute;i đối với cha m&amp;igrave;nh. Đ&amp;acirc;y vừa l&amp;agrave; n&amp;eacute;t đẹp trong c&amp;aacute;ch đối nh&amp;acirc;n xử thế, vừa l&amp;agrave; ng&amp;agrave;y gắn kết t&amp;igrave;nh cảm giữa những người th&amp;acirc;n trong gia đ&amp;igrave;nh với nhau, gi&amp;uacute;p gia đ&amp;igrave;nh th&amp;ecirc;m y&amp;ecirc;u thương v&amp;agrave; hiểu nhau hơn.&lt;/p&gt;\r\n&lt;p&gt;Mục đ&amp;iacute;ch của ng&amp;agrave;y n&amp;agrave;y l&amp;agrave; c&amp;ugrave;ng với Ng&amp;agrave;y của Mẹ, con c&amp;aacute;i c&amp;oacute; dịp thể hiện v&amp;agrave; b&amp;agrave;y tỏ t&amp;igrave;nh y&amp;ecirc;u thương, k&amp;iacute;nh trọng với những người cha của m&amp;igrave;nh. Tr&amp;ecirc;n thế giới ngo&amp;agrave;i ng&amp;agrave;y Chủ nhật thứ 3 của th&amp;aacute;ng 6 th&amp;igrave; c&amp;ograve;n c&amp;oacute; nhiều ng&amp;agrave;y kỷ niệm d&amp;agrave;nh cho cha nhưng n&amp;oacute;i chung, trong bất kỳ ng&amp;agrave;y kỷ niệm n&amp;agrave;o đều c&amp;oacute; hoa, qu&amp;agrave; tặng, bữa tối ấm c&amp;uacute;ng c&amp;ugrave;ng cha v&amp;agrave; những hoạt động gia đ&amp;igrave;nh th&amp;acirc;n mật.&lt;/p&gt;\r\n&lt;p&gt;Ng&amp;agrave;y của Cha được tổ chức rộn r&amp;agrave;ng nhất vẫn l&amp;agrave; ở Mỹ. Khắp c&amp;aacute;c bang đều c&amp;oacute; lễ hội trong ng&amp;agrave;y n&amp;agrave;y. Trẻ con xuống đường diễu h&amp;agrave;nh, k&amp;egrave;n trống tưng bừng trong &amp;aacute;nh mắt h&amp;atilde;nh diện của người lớn. Ở lễ hội ch&amp;iacute;nh của th&amp;agrave;nh phố Kennewick (bang Washington), trẻ em sẽ tha hồ vui chơi như trong Ng&amp;agrave;y quốc tế thiếu nhi; tranh t&amp;agrave;i trong c&amp;aacute;c tr&amp;ograve; chơi vận động&amp;hellip;&lt;/p&gt;\r\n&lt;div class=&quot;content-detail&quot;&gt;\r\n&lt;p&gt;Ở một số nước kh&amp;aacute;c, Ng&amp;agrave;y của Cha kh&amp;ocirc;ng rầm rộ như ở Mỹ nhưng qu&amp;agrave; tặng cha cũng được b&amp;agrave;y b&amp;aacute;n khắp nơi v&amp;agrave; tinh thần của ng&amp;agrave;y n&amp;agrave;y được c&amp;aacute;c phương tiện truyền th&amp;ocirc;ng nhắc nhở. Tại Nhật, một số cửa h&amp;agrave;ng rao b&amp;aacute;n c&amp;aacute;c sản phẩm d&amp;agrave;nh cho cha như gối ngủ tr&amp;ecirc;n m&amp;aacute;y bay, thẻ thể dục thể h&amp;igrave;nh, t&amp;uacute;i đựng thuốc vitamin, CD nhạc nhẹ, gi&amp;agrave;y thể thao, k&amp;iacute;nh r&amp;acirc;m, cần c&amp;acirc;u, dụng cụ l&amp;agrave;m vườn&amp;hellip; TạiCanada, những tấm gương l&amp;agrave;m cha mẫu mực được t&amp;ocirc;n vinh tr&amp;ecirc;n b&amp;aacute;o&amp;hellip;&lt;/p&gt;\r\n&lt;p&gt;Tại&amp;nbsp;Việt Nam, mặc d&amp;ugrave; Ng&amp;agrave;y của Cha chưa ch&amp;iacute;nh thức trở th&amp;agrave;nh lễ kỷ niệm tr&amp;ecirc;n to&amp;agrave;n quốc nhưng nhiều bạn trẻ đ&amp;atilde; hưởng ứng ch&amp;agrave;o mừng. Nhiều người nghỉ ph&amp;eacute;p về qu&amp;ecirc; thăm gia đ&amp;igrave;nh v&amp;agrave; tỏ l&amp;ograve;ng t&amp;ocirc;n k&amp;iacute;nh đến người cha thương y&amp;ecirc;u. Một số người đưa gia đ&amp;igrave;nh, cha đi ăn tối ở một nh&amp;agrave; h&amp;agrave;ng hoặc mua những m&amp;oacute;n qu&amp;agrave; nhỏ, &amp;yacute; nghĩa tặng cha như một c&amp;aacute;i c&amp;agrave; vạt, c&amp;aacute;i &amp;aacute;o, k&amp;iacute;nh m&amp;aacute;t&amp;hellip;&lt;/p&gt;\r\n&lt;p&gt;Kh&amp;ocirc;ng c&amp;oacute; niềm vui n&amp;agrave;o s&amp;aacute;nh được với nụ cười của cha mẹ khi con c&amp;aacute;i lu&amp;ocirc;n ngoan ngo&amp;atilde;n v&amp;agrave; th&amp;agrave;nh đạt. Vậy th&amp;igrave;, h&amp;atilde;y biến bất cứ một ng&amp;agrave;y b&amp;igrave;nh thường n&amp;agrave;o cũng trở th&amp;agrave;nh Ng&amp;agrave;y của Cha. H&amp;atilde;y y&amp;ecirc;u thương, tr&amp;acirc;n trọng những ph&amp;uacute;t gi&amp;acirc;y b&amp;ecirc;n cha, b&amp;ecirc;n gia đ&amp;igrave;nh, đừng để đến l&amp;uacute;c nhận ra gi&amp;aacute; trị đ&amp;iacute;ch thực của cha, của gia đ&amp;igrave;nh trong cuộc sống th&amp;igrave; mọi thứ đ&amp;atilde; muộn m&amp;agrave;ng.&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;author-info clearfix&quot; style=&quot;text-align: right;&quot;&gt;&lt;strong&gt;&lt;span class=&quot;author&quot;&gt;Theo Đời Sống Ph&amp;aacute;p Luật&lt;/span&gt;&lt;/strong&gt;&lt;/div&gt;', 'đời sống, pháp luật, cha, dailyaz', '2018-03-10 08:18:19', '2018-03-10 08:18:19', 1, 7, 2, 0, 0, 1, 1),
(3, 'THÔNG BÁO: DỪNG XÁC NHẬN ĐƠN HÀNG ONLINE TỪ NGÀY 15/01/2017', '&lt;p&gt;Tết Đinh Dậu đang đến gần, tất cả ch&amp;uacute;ng ta đều bận rộn với c&amp;ocirc;ng việc, với kế hoạch sắm sửa Tết nguy&amp;ecirc;n đ&amp;aacute;n. Để gi&amp;uacute;p Qu&amp;yacute; kh&amp;aacute;ch c&amp;oacute; kế hoạch mua sắm sớm hơn nhằm gi', 'Kawasaki_Z1000_2017_zing_13_1.jpg', '&lt;p&gt;Tết Đinh Dậu đang đến gần, tất cả ch&amp;uacute;ng ta đều bận rộn với c&amp;ocirc;ng việc, với kế hoạch sắm sửa Tết nguy&amp;ecirc;n đ&amp;aacute;n. Để gi&amp;uacute;p Qu&amp;yacute; kh&amp;aacute;ch c&amp;oacute; kế hoạch mua sắm sớm hơn nhằm giảm tải c&amp;ocirc;ng việc nhiều về cuối năm v&amp;agrave; để phục vụ tốt hơn cho Qu&amp;yacute; kh&amp;aacute;ch, Lee&amp;amp;Tee xin ph&amp;eacute;p tạm ngưng nhận xử l&amp;yacute; đơn đặt h&amp;agrave;ng Online tr&amp;ecirc;n website&amp;nbsp;&lt;a href=&quot;http://leeandtee.vn/undefined/&quot;&gt;http://leeandtee.vn&lt;/a&gt;&amp;nbsp;kể từ ng&amp;agrave;y 15/01/2017.&lt;/p&gt;\r\n&lt;p&gt;Mong Qu&amp;yacute; kh&amp;aacute;ch th&amp;ocirc;ng cảm v&amp;igrave; sự thay đổi tr&amp;ecirc;n.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/tam-dung-order-1200x1200.jpg&quot; /&gt;&lt;/p&gt;', 'thông báo', '2018-03-10 08:55:00', '2018-03-10 08:55:00', 1, 7, 0, 0, 0, 1, 1),
(4, 'Thương hiệu túi xách Lee&amp;Tee ra mắt bộ sưu tập mới trong chương trình Phong cách và Cuộc sống', '&lt;p&gt;Thương hiệu t&amp;uacute;i x&amp;aacute;ch Lee&amp;amp;Tee đ&amp;atilde; cho ra mắt bộ sưu tập t&amp;uacute;i x&amp;aacute;ch h&amp;egrave; được tr&amp;igrave;nh diễn trong chương tr&amp;igrave;nh Phong c&amp;aacute;ch &amp;amp; Cuộc sống th&amp;', 'jUdE0.jpg', '&lt;p align=&quot;center&quot;&gt;Thương hiệu t&amp;uacute;i x&amp;aacute;ch Lee&amp;amp;Tee đ&amp;atilde; cho ra mắt bộ sưu tập t&amp;uacute;i x&amp;aacute;ch h&amp;egrave; được tr&amp;igrave;nh diễn trong chương tr&amp;igrave;nh Phong c&amp;aacute;ch &amp;amp; Cuộc sống th&amp;aacute;ng 5 với chủ đề &quot;Nắng hạ&quot; tối 17-5 vừa qua. &amp;nbsp;Trong đ&amp;ecirc;m thời trang n&amp;agrave;y, h&amp;agrave;ng loạt c&amp;aacute;c mẫu t&amp;uacute;i x&amp;aacute;ch Lee&amp;amp;Tee đ&amp;atilde; được c&amp;aacute;c người đẹp, si&amp;ecirc;u mẫu tr&amp;igrave;nh l&amp;agrave;ng c&amp;ugrave;ng những bộ c&amp;aacute;nh thời trang s&amp;agrave;nh điệu.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/1.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;Lee&amp;amp;Tee được biết đến l&amp;agrave; thương hiệu t&amp;uacute;i x&amp;aacute;ch với hơn 5 năm hoạt động, l&amp;agrave; top 10 thương hiệu nổi tiếng do hội sở hữu tr&amp;iacute; tuệ việt nam b&amp;igrave;nh chọn.&lt;/p&gt;\r\n&lt;div class=&quot;_5wd4 _1nc7 direction_ltr&quot;&gt;\r\n&lt;div class=&quot;_5wd9&quot;&gt;\r\n&lt;div class=&quot;_5wde _n4o&quot;&gt;\r\n&lt;div class=&quot;_5w1r _3_om _5wdf&quot;&gt;\r\n&lt;div class=&quot;_4gx_&quot;&gt;\r\n&lt;div class=&quot;_d97&quot;&gt;&lt;span class=&quot;_5yl5&quot;&gt;Với thiết kế đơn giản nhưng tinh tế c&amp;ugrave;ng gam m&amp;agrave;u trầm cổ điển mang đến sự đặc biệt trong sản phẩm, tăng th&amp;ecirc;m sự sang trọng cho chủ thể.&amp;nbsp;&lt;/span&gt;B&amp;ecirc;n cạnh đ&amp;oacute;, sự tiện dụng, ph&amp;ugrave; hợp với nhiều lứa tuổi v&amp;agrave; phong c&amp;aacute;ch thời trang kh&amp;aacute;c nhau cũng l&amp;agrave; ưu điểm quan trọng l&amp;agrave;m n&amp;ecirc;n thương hiệu Lee&amp;amp;Tee.&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;Bộ sưu tập t&amp;uacute;i x&amp;aacute;ch với chủ đề&amp;nbsp;&lt;strong&gt;Nắng hạ&lt;/strong&gt;&amp;nbsp;từ thương hiệu Lee&amp;amp;Tee bao gồm những mẫu thiết kế theo xu hướng&amp;nbsp;mới lạ m&amp;agrave; kh&amp;ocirc;ng k&amp;eacute;m phần độc đ&amp;aacute;o.&amp;nbsp;Bất chấp những chuẩn mực, những kiểu d&amp;aacute;ng thường thấy của những mẫu t&amp;uacute;i x&amp;aacute;ch quen thuộc, Lee&amp;amp;Tee của th&amp;aacute;ng 5 l&amp;agrave; tổng hợp của sự ph&amp;aacute; c&amp;aacute;ch để tạo n&amp;ecirc;n những mẫu t&amp;uacute;i cực chất chưa b&amp;agrave;o giờ xuất hiện tr&amp;ecirc;n thị trường Việt Nam&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/6.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;Những chiếc t&amp;uacute;i du lịch với nhiều k&amp;iacute;ch thước sẽ l&amp;agrave; sự lựa chọn ho&amp;agrave;n hảo cho những chuyến du lịch ngắn ng&amp;agrave;y c&amp;ugrave;ng bạn b&amp;egrave; hoặc trong những chuyến đi c&amp;ocirc;ng t&amp;aacute;c. Với chất liệu da tổng hợp cao cấp, bền bỉ, chống thấm nước, t&amp;uacute;i du lịch Lee&amp;amp;Tee sẽ gi&amp;uacute;p bạn tự tin, thoải m&amp;aacute;i chống lại c&amp;aacute;i nắng bỏng r&amp;aacute;t hay những cơn mưa bất chợt trong m&amp;ugrave;a h&amp;egrave;.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/20.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/19.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;Mẫu t&amp;uacute;i du lịch Lee&amp;amp;Tee&lt;/p&gt;\r\n&lt;p&gt;Nếu bạn l&amp;agrave; một nh&amp;acirc;n vi&amp;ecirc;n văn ph&amp;ograve;ng th&amp;igrave; cũng đừng bỏ qua bộ sưu tập n&amp;agrave;y nh&amp;eacute;. Trong bộ sưu tập Nắng Hạ,&amp;nbsp;&lt;strong&gt;Lee&amp;amp;Tee&lt;/strong&gt;&amp;nbsp;đ&amp;atilde; cho ra mắt những mẫu t&amp;uacute;i x&amp;aacute;ch cầm tay, đeo vai thiết kế đơn giản vừa vặn cho chiếc latop hay tập t&amp;agrave;i liệu v&amp;ocirc; c&amp;ugrave;ng tiện dụng, t&amp;uacute;i x&amp;aacute;ch kiểu d&amp;aacute;ng trẻ trung thanh lịch sẽ gi&amp;uacute;p bạn tự tin, mạnh mẽ nơi c&amp;ocirc;ng sở.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/3.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/23.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/26.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/25.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/24.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;C&amp;aacute;c mẫu t&amp;uacute;i c&amp;ocirc;ng sở&lt;/p&gt;\r\n&lt;p&gt;Những chiếc t&amp;uacute;i đeo ch&amp;eacute;o, balo nhỏ nhắn xinh xắn thiết kế trẻ trung, năng động c&amp;ugrave;ng bạn dạo phố cũng g&amp;oacute;p phần l&amp;agrave;m n&amp;ecirc;n bộ sưu tập Nắng Hạ lần n&amp;agrave;y.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/12.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/15.jpg&quot; width=&quot;80%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;Ch&amp;iacute;nh những thiết kế lấy sự đơn giản nhưng tinh tế l&amp;agrave;m nền tảng ấy đ&amp;atilde; khiến kh&amp;aacute;ch h&amp;agrave;ng khi đến với Lee&amp;amp;Tee v&amp;ocirc; c&amp;ugrave;ng h&amp;agrave;i l&amp;ograve;ng v&amp;agrave; tin tưởng . Điều đ&amp;oacute; g&amp;oacute;p phần tạo n&amp;ecirc;n&lt;strong&gt;&amp;nbsp;thương hiệu t&amp;uacute;i x&amp;aacute;ch Lee&amp;amp;Tee &amp;ndash; một thương hiệu&amp;nbsp; t&amp;uacute;i x&amp;aacute;ch của người Việt.&lt;/strong&gt;&lt;/p&gt;', 'hướng dẫn, dailyaz', '2018-03-10 08:57:55', '2018-03-10 08:57:55', 1, 7, 0, 0, 0, 1, 1),
(5, 'Túi xách mùa hè tràn ngập sàn diễn thời trang', '&lt;p&gt;Trong đ&amp;ecirc;m thời trang Phong c&amp;aacute;ch &amp;amp; Cuộc sống diễn ra tối 17-5, h&amp;agrave;ng loạt c&amp;aacute;c mẫu t&amp;uacute;i x&amp;aacute;ch với chất liệu bằng da tổng hợp đ&amp;atilde; được c&amp;aacute;c người đẹp, si&amp;e', 'tui-xach-pccs-nguoinoitieng-15.jpg', '&lt;p&gt;&lt;strong&gt;Trong đ&amp;ecirc;m thời trang Phong c&amp;aacute;ch &amp;amp; Cuộc sống diễn ra tối 17-5, h&amp;agrave;ng loạt c&amp;aacute;c mẫu t&amp;uacute;i x&amp;aacute;ch với chất liệu bằng da tổng hợp đ&amp;atilde; được c&amp;aacute;c người đẹp, si&amp;ecirc;u mẫu tr&amp;igrave;nh l&amp;agrave;ng c&amp;ugrave;ng những bộ c&amp;aacute;nh thời trang s&amp;agrave;nh điệu.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/tui-xach-pccs-nguoinoitieng-15.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;Ninh Ho&amp;agrave;ng Ng&amp;acirc;n - Hữu Long&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;Những mẫu t&amp;uacute;i x&amp;aacute;ch đẹp mắt, mới nhất, dễ phối đồ, ph&amp;ugrave; hợp với mọi lứa tuổi đ&amp;atilde; được giới thiệu đến với c&amp;aacute;c t&amp;iacute;n đồ đam m&amp;ecirc; thời trang trong chương tr&amp;igrave;nh Phong c&amp;aacute;ch &amp;amp; Cuộc sống th&amp;aacute;ng 5 với chủ đề &quot;Nắng hạ&quot;.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/tui-xach-pccs-nguoinoitieng-12.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;Nhiko Dinh - Kim Dung&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/tui-xach-pccs-nguoinoitieng-13.jpg&quot; /&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;&lt;img src=&quot;http://leeandtee.vn/uploads/images/Phong_cach_va_cuoc_song_17-5/tui-xach-pccs-nguoinoitieng-14.jpg&quot; /&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;Thanh Thức - Thanh Tr&amp;uacute;c&lt;/em&gt;&lt;/p&gt;\r\n&l