INSERT INTO `content` (`con_id`, `con_title`, `con_intro`, `con_image`, `con_detail`, `con_tags`, `con_createdate`, `con_editdate`, `con_type`, `con_catid`, `con_view`, `con_interesting`, `con_vote`, `con_publish`, `con_user`) VALUES
(15, 'Hai thái cực thời trang của ca sĩ Hương Giang Idol: kiểu nào cũng Đẹp', '&lt;p&gt;Lu&amp;ocirc;n xuất hiện lộng lẫy như c&amp;ocirc;ng ch&amp;uacute;a tr&amp;ecirc;n s&amp;acirc;n khấu, nhưng ngo&amp;agrave;i đời ca sĩ Hương Giang Idol lại giản dị với phong c&amp;aacute;ch thời trang trẻ trung, hiện đại.&lt;/p&gt;', 'i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__8_.jpg', '&lt;p&gt;Lu&amp;ocirc;n xuất hiện lộng lẫy như c&amp;ocirc;ng ch&amp;uacute;a tr&amp;ecirc;n s&amp;acirc;n khấu, nhưng ngo&amp;agrave;i đời ca sĩ Hương Giang Idol lại giản dị với phong c&amp;aacute;ch thời trang trẻ trung, hiện đại.&lt;/p&gt;\r\n&lt;p&gt;Ch&amp;agrave;o năm mới, ca sĩ Hương Giang Idol g&amp;acirc;y bất ngờ với người h&amp;acirc;m mộ khi trở th&amp;agrave;nh đại diện Việt Nam thi &quot;Hoa hậu chuyển giới thế giới&quot; tại Th&amp;aacute;i Lan. Th&amp;ocirc;ng tin n&amp;agrave;y khiến c&amp;aacute;c fan kh&amp;ocirc;ng khỏi ngạc nhi&amp;ecirc;n v&amp;agrave; th&amp;iacute;ch th&amp;uacute;. B&amp;ecirc;n cạnh giọng ca ngọt ng&amp;agrave;o, vẻ ngo&amp;agrave;i gợi cảm, Hương Giang Idol c&amp;ograve;n sở hữu gu thời trang trẻ trung, hiện đại.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__8_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;Điều th&amp;uacute; vị l&amp;agrave; xuất hiện tr&amp;ecirc;n s&amp;acirc;n khấu lộng lẫy như nữ ho&amp;agrave;ng bao nhi&amp;ecirc;u, th&amp;igrave; ngo&amp;agrave;i đời Hương Giang Idol lại giản dị trong những set đồ c&amp;ocirc;ng sở dễ mặc bấy nhi&amp;ecirc;u. H&amp;ocirc;m nay n&amp;agrave;ng h&amp;atilde;y c&amp;ugrave;ng Juno kh&amp;aacute;m ph&amp;aacute; hai sắc th&amp;aacute;i thời trang của Hương Gian Idol nh&amp;eacute;.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__11_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: 11pt;&quot;&gt;&lt;strong&gt;Hương Giang Idol lộng lẫy như nữ ho&amp;agrave;ng với trang phục biểu diễn&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Hương Giang Idol lu&amp;ocirc;n đầu tư cho trang phục biểu diễn với v&amp;aacute;y &amp;aacute;o lộng lẫy v&amp;agrave; vẻ ngo&amp;agrave;i trưởng th&amp;agrave;nh v&amp;agrave; gợi cảm. Trang phục &amp;aacute;nh kim &amp;ocirc;m s&amp;aacute;t lu&amp;ocirc;n gi&amp;uacute;p ca sĩ khoe trọn vẻ đẹp của m&amp;igrave;nh.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/huong-giang-idol__615_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__12_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;Đầm xẻ ngực s&amp;acirc;u, đầm s&amp;aacute;ng v&amp;aacute;y đu&amp;ocirc;i c&amp;aacute; lu&amp;ocirc;n được Hương Giang Idol chọn để khoe trọn vẻ đẹp gợi cảm.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: 11pt;&quot;&gt;&lt;strong&gt;Hương Giang Idol giản dị, trẻ trung với trang phục đời thường&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Rời s&amp;acirc;n khấu, Hương Giang Idol trở về với gu thời trang đơn giản, nhẹ nh&amp;agrave;ng. Nữ ca sĩ thường xuy&amp;ecirc;n xuất hiện trong trang phục thoải m&amp;aacute;i như gi&amp;agrave;y đế bệt, quần d&amp;agrave;i, &amp;aacute;o sơ mi.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__2_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;Hương Giang Idol xuống phố với chiếc &amp;aacute;o hồng phấn c&amp;aacute;ch điệu, quần culottes m&amp;agrave;u đen tiệp m&amp;agrave;u với t&amp;uacute;i v&amp;agrave; gi&amp;agrave;y.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__5_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;T&amp;uacute;i x&amp;aacute;ch trung nắp giả phối kh&amp;oacute;a bấm kim loại TXT110&quot; href=&quot;https://juno.vn/products/tui-xach-trung-txt110&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/mau-tui-xach-giay-moi-bst-juno-queens-of-fashion-2017__66_.jpg&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;&lt;em&gt;(Click v&amp;agrave;o để xem:&amp;nbsp;&lt;a title=&quot;T&amp;uacute;i x&amp;aacute;ch trung nắp giả phối kh&amp;oacute;a bấm kim loại TXT110&quot; href=&quot;https://juno.vn/products/tui-xach-trung-txt110&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;T&amp;uacute;i x&amp;aacute;ch trung nắp giả phối kh&amp;oacute;a bấm kim loại TXT110&lt;/a&gt;)&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;T&amp;uacute;i x&amp;aacute;ch m&amp;agrave;u đen l&amp;agrave; phụ kiện được Hương Giang Idol y&amp;ecirc;u th&amp;iacute;ch, c&amp;oacute; lẽ v&amp;igrave; đ&amp;acirc;y l&amp;agrave; item gi&amp;uacute;p c&amp;ocirc; n&amp;agrave;ng dễ d&amp;agrave;ng kết hợp với nhiều trang phục kh&amp;aacute;c nhau.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__3_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;Điểm th&amp;uacute; vị của set đồ l&amp;agrave; sử dụng 2 gam m&amp;agrave;u chủ đạo l&amp;agrave; v&amp;agrave;ng v&amp;agrave; đen, nhưng kh&amp;ocirc;ng hề đơn điệu bởi chi tiết đ&amp;ocirc;i tất cao m&amp;agrave;u v&amp;agrave;ng vui mắt.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;Th&amp;ecirc;m một gợi &amp;yacute; hay ho cho n&amp;agrave;ng c&amp;ocirc;ng sở đến từ Hương Giang Idol, &amp;aacute;o kho&amp;aacute;c blazer, kết hợp với &amp;aacute;o thun crop top khoe eo thon c&amp;ugrave;ng quần su&amp;ocirc;ng ống rộng.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__4_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;Giày cao g&amp;oacute;t 11cm mũi nhọn &amp;aacute;nh kim tuyến CG11003&quot; href=&quot;https://juno.vn/products/giay-cao-got-cg11003&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/mau-tui-xach-giay-moi-bst-juno-queens-of-fashion-2017-_6_.jpg&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;(Click v&amp;agrave;o để xem:&amp;nbsp;&lt;a title=&quot;Giày cao g&amp;oacute;t 11cm mũi nhọn &amp;aacute;nh kim tuyến CG11003&quot; href=&quot;https://juno.vn/products/giay-cao-got-cg11003&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;Giày cao g&amp;oacute;t 11cm mũi nhọn &amp;aacute;nh kim tuyến CG11003&lt;/a&gt;)&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;N&amp;agrave;ng nhớ mang c&amp;ugrave;ng đ&amp;ocirc;i gi&amp;agrave;y cao g&amp;oacute;t để đ&amp;ocirc;i ch&amp;acirc;n kh&amp;ocirc;ng bị nuốt chửng trong chiếc quần d&amp;agrave;i mi&amp;ecirc;n man. Gam m&amp;agrave;u x&amp;aacute;m, đen xuy&amp;ecirc;n suốt gi&amp;uacute;p to&amp;aacute;t l&amp;ecirc;n vẻ ngo&amp;agrave;i thanh lịch.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;Aacute;o kho&amp;aacute;c blazer tiếp tục được Hương Giang Idol kết hợp với &amp;aacute;o thun họa tiết v&amp;agrave; quần jeans trẻ trung, hay đơn giản l&amp;agrave; phối với &amp;aacute;o sơ mi họa tiết tr&amp;aacute;i tim thanh lịch dạo phố.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__6_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__7_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;C&amp;oacute; thể dễ d&amp;agrave;ng nhận ra ca sĩ Hương Giang Idol lu&amp;ocirc;n chọn cho m&amp;igrave;nh những trang phục thoải m&amp;aacute;i c&amp;oacute; t&amp;iacute;nh ứng dụng cao để lu&amp;ocirc;n được l&amp;agrave; m&amp;igrave;nh trong mọi ho&amp;agrave;n cảnh.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/i-thai-cuc-thoi-trang-cua-ca-si-huong-giang-idol-kieu-nao-cung-dep__1_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;Gi&amp;agrave;y b&amp;uacute;p b&amp;ecirc; mũi tr&amp;ograve;n trang tr&amp;iacute; nơ kim loại BB01111&quot; href=&quot;https://juno.vn/products/giay-bup-be-bb01111&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/huong-giang-idol-1.jpg&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;(Click v&amp;agrave;o để xem:&amp;nbsp;&lt;a title=&quot;Gi&amp;agrave;y b&amp;uacute;p b&amp;ecirc; mũi tr&amp;ograve;n trang tr&amp;iacute; nơ kim loại BB01111&quot; href=&quot;https://juno.vn/products/giay-bup-be-bb01111&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;Gi&amp;agrave;y b&amp;uacute;p b&amp;ecirc; mũi tr&amp;ograve;n trang tr&amp;iacute; nơ kim loại BB01111&lt;/a&gt;)&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;T&amp;uacute;i x&amp;aacute;ch m&amp;agrave;u đen, gi&amp;agrave;y bệt hay gi&amp;agrave;y cao g&amp;oacute;t trung t&amp;iacute;nh lu&amp;ocirc;n l&amp;agrave; phụ kiện trung th&amp;agrave;nh của những c&amp;ocirc; n&amp;agrave;ng y&amp;ecirc;u phong c&amp;aacute;ch thời trang đơn giản. N&amp;agrave;ng c&amp;oacute; th&amp;iacute;ch phong c&amp;aacute;ch thời trang của Hương Giang Idol kh&amp;ocirc;ng? Like v&amp;agrave; share ủng hộ n&amp;agrave;o!&lt;/p&gt;', 'hương giang idol, dailyaz, túi xách', '2018-03-10 10:23:43', '2018-03-10 10:23:43', 1, 7, 0, 0, 0, 0, 1),
(16, 'Nếu quả thực Tím là màu hot nhất năm 2018, thì đây là 5 giải pháp giúp tím bớt “sến”', '&lt;p&gt;Kh&amp;ocirc;ng qu&amp;aacute; kh&amp;oacute; phối đồ như m&amp;agrave;u t&amp;iacute;m đậm, t&amp;iacute;m lavender &amp;nbsp;mang &amp;acirc;m hưởng của những gam m&amp;agrave;u pastel ngọt ng&amp;agrave;o. Với sắc th&amp;aacute;i the dịu, t&am', 'au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__15_.jpg', '&lt;p&gt;Kh&amp;ocirc;ng qu&amp;aacute; kh&amp;oacute; phối đồ như m&amp;agrave;u t&amp;iacute;m đậm, t&amp;iacute;m lavender &amp;nbsp;mang &amp;acirc;m hưởng của những gam m&amp;agrave;u pastel ngọt ng&amp;agrave;o. Với sắc th&amp;aacute;i the dịu, t&amp;iacute;m lavender &amp;nbsp;gi&amp;uacute;p c&amp;aacute;c c&amp;ocirc; g&amp;aacute;i tỏa ra n&amp;eacute;t đẹp nữ t&amp;iacute;nh quyến rũ. Đặc biệt khi đi c&amp;ugrave;ng chất liệu mềm mại, nhẹ nh&amp;agrave;ng như voan, nhung, &amp;aacute;nh kim, t&amp;iacute;m lavender &amp;nbsp;khai th&amp;aacute;c tối đa n&amp;eacute;t nữ t&amp;iacute;nh của người thiếu nữ.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__15_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;H&amp;ocirc;m nay n&amp;agrave;ng h&amp;atilde;y c&amp;ugrave;ng Juno kh&amp;aacute;m ph&amp;aacute; những thiết kế &amp;aacute;p dụng với t&amp;iacute;m lavender để n&amp;agrave;ng vẫn lu&amp;ocirc;n đẹp m&amp;agrave; kh&amp;ocirc;ng &amp;ldquo;sến&amp;rdquo; như lời đồn đại về m&amp;agrave;u t&amp;iacute;m &amp;ldquo;rịm&amp;rdquo;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__10_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;h3&gt;&lt;strong&gt;T&amp;iacute;m lavender với kiểu d&amp;aacute;ng đơn giản&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Phối đồ nguy&amp;ecirc;n c&amp;acirc;y m&amp;agrave;u t&amp;iacute;m lavender lu&amp;ocirc;n l&amp;agrave; thử th&amp;aacute;ch kh&amp;oacute; khăn. L&amp;uacute;c n&amp;agrave;y n&amp;agrave;ng c&amp;oacute; thể t&amp;igrave;m đến trang phục c&amp;oacute; kiểu d&amp;aacute;ng đơn giản như đầm su&amp;ocirc;ng, suit.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__10_.jpeg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/mau-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__9_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;h3&gt;&lt;strong&gt;T&amp;iacute;m lavender đi c&amp;ugrave;ng m&amp;agrave;u trắng&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Một chiếc đầm m&amp;agrave;u t&amp;iacute;m lavender c&amp;aacute;ch điệu sẽ bớt sến s&amp;uacute;a khi đi c&amp;ugrave;ng phụ kiện gi&amp;agrave;y cao g&amp;oacute;t hoặc gi&amp;agrave;y xăng đan m&amp;agrave;u trắng. Gam m&amp;agrave;u trắng nền n&amp;atilde; gi&amp;uacute;p trung h&amp;ograve;a gam t&amp;iacute;m, tạo n&amp;ecirc;n tổng thể thanh lịch cho người mặc.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__11_.jpeg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;Gi&amp;agrave;y Sandal cao g&amp;oacute;t 7 cm mũi tr&amp;ograve;n SD0701&quot; href=&quot;https://juno.vn/products/giay-sandal-sd07015&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/xanh-duong_sd07015_1.jpg&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;(Click v&amp;agrave;o để xem sản phẩm tham khảo:&amp;nbsp;&lt;a title=&quot;Gi&amp;agrave;y Sandal cao g&amp;oacute;t 7 cm mũi tr&amp;ograve;n SD0701&quot; href=&quot;https://juno.vn/products/giay-sandal-sd07015&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;Gi&amp;agrave;y Sandal cao g&amp;oacute;t 7 cm mũi tr&amp;ograve;n SD0701&lt;/a&gt;5)&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/mau-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__8_.jpeg&quot; /&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;h3&gt;&lt;strong&gt;C&amp;ugrave;ng chất liệu m&amp;agrave;u t&amp;iacute;m lavender&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Sự h&amp;ograve;a hợp của nhiều chất liệu c&amp;ugrave;ng m&amp;agrave;u t&amp;iacute;m đặt cạnh nhau cho hiệu ứng đẹp mắt. Tuy điều n&amp;agrave;y y&amp;ecirc;u cầu người phối đồ c&amp;oacute; kỹ thuật cao v&amp;agrave; nắm chắc về chất liệu, đổi lại n&amp;agrave;ng sẽ c&amp;oacute; một set đồ với c&amp;aacute; t&amp;iacute;nh đậm n&amp;eacute;t.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__14_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/bac_cg09041_1_69aa6914ebfc48c190ba7edf01fed56a.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;&lt;em&gt;(Click v&amp;agrave;o để xem sản phẩm tham khảo:&amp;nbsp;&lt;a title=&quot;Gi&amp;agrave;y sandal 9cm mũi tr&amp;ograve;n g&amp;oacute;t nhọn phối kim tuyến SD09041&quot; href=&quot;https://juno.vn/products/giay-sandal-sd09041&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;Gi&amp;agrave;y sandal 9cm mũi tr&amp;ograve;n g&amp;oacute;t nhọn phối kim tuyến SD09041&lt;/a&gt;)&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;Đặc biệt chất liệu &amp;aacute;nh kim xuy&amp;ecirc;n suốt c&amp;ugrave;ng chiếc đầm &amp;ocirc;m s&amp;aacute;t t&amp;ocirc; đậm vẻ gợi cảm, qu&amp;yacute; ph&amp;aacute;i của người mặc.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__17_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__13_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Ch&amp;acirc;n v&amp;aacute;y x&amp;ograve;e vải kết hợp c&amp;ugrave;ng t&amp;uacute;i x&amp;aacute;ch đ&amp;atilde; qua xử l&amp;yacute; acid wash mang tới vẻ đẹp ph&amp;oacute;ng kho&amp;aacute;ng, l&amp;atilde;ng mạn cho tổng thể.&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__16_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;h3&gt;&lt;strong&gt;Trang sức tối giản kết hợp với m&amp;agrave;u t&amp;iacute;m valender&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Gam m&amp;agrave;u t&amp;iacute;m valender đi c&amp;ugrave;ng thiết kế dự tiệc đẹp m&amp;ecirc; hoặc. Đặc biệt đi c&amp;ugrave;ng chất liệu voan bồng bềnh quyến rũ.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__11_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Xăng đan m&amp;agrave;u nude, phụ kiện &amp;iacute;t ỏi thậm ch&amp;iacute; kh&amp;ocirc;ng cần đeo, n&amp;agrave;ng vẫn tỏa ra sức h&amp;uacute;t rất ri&amp;ecirc;ng trong gam m&amp;agrave;u t&amp;iacute;m valender.&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/mau-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__9_.jpeg&quot; /&gt;&lt;/p&gt;\r\n&lt;h3&gt;&lt;strong&gt;Đường cắt tăng n&amp;eacute;t khỏe khoắn cho m&amp;agrave;u t&amp;iacute;m valender&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Việc khai th&amp;aacute;c nhiều đường cắt, đường may vừa g&amp;oacute;p phần định h&amp;igrave;nh d&amp;aacute;ng v&amp;aacute;y, vừa tăng vẻ ngo&amp;agrave;i mạnh mẽ cho trang phục gi&amp;uacute;p chiếc v&amp;aacute;y của n&amp;agrave;ng c&amp;aacute; t&amp;iacute;nh hơn, mạnh mẽ hơn rất nhiều.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__18_.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/au-hot-nhat-nam-2018-thi-day-la-5-gam-mau-che-ngu-mau-tim-bot-sen__12_.jpg&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: 400;&quot;&gt;Juno mong rằng sau b&amp;agrave;i viết n&amp;agrave;y, n&amp;agrave;ng đ&amp;atilde; c&amp;oacute; trong tay những c&amp;aacute;ch đơn giản, dễ l&amp;agrave;m để chinh phục m&amp;agrave;u &amp;nbsp;t&amp;iacute;m valender. Gam m&amp;agrave;u n&amp;agrave;y đi ra từ m&amp;agrave;u pastel, sẽ mang tới cho n&amp;agrave;ng năm mới ngọt ng&amp;agrave;o, tươi trẻ. Đặc biệt đi c&amp;ugrave;ng phụ kiện m&amp;agrave;u trung t&amp;iacute;nh như trắng, kem, x&amp;aacute;m của Juno.&lt;/span&gt;&lt;/p&gt;', 'màu tím, dailyaz, thời trang, ví túi', '2018-03-10 10:25:21', '2018-03-10 10:25:21', 1, 7, 0, 0, 0, 0, 1),
(17, 'Đỏ - Gam màu được các nhà Mốt đồng loạt lăng xê cuối năm 2017', '&lt;p&gt;Ngay sau khi c&amp;aacute;c nh&amp;agrave; mốt đồng loạt tung ra BST Thu - Đ&amp;ocirc;ng 2017 của m&amp;igrave;nh, l&amp;agrave;ng thời trang thế giới nhanh ch&amp;oacute;ng nhận ra điểm chung trong c&amp;aacute;c BST đ&amp;oacute; ch&amp;iacute', '3c678b8f463add3e84e10c66283adc3a_1024x1024.jpg', '&lt;p&gt;NTK thời trang Bill Blass từng n&amp;oacute;i rằng &quot;Khi cảm thấy lưỡng lự kh&amp;ocirc;ng biết chọn m&amp;agrave;u g&amp;igrave;, h&amp;atilde;y mặc m&amp;agrave;u đỏ&quot;.&lt;/p&gt;\r\n&lt;p&gt;Ngay sau khi c&amp;aacute;c nh&amp;agrave; mốt đồng loạt tung ra BST Thu - Đ&amp;ocirc;ng 2017 của m&amp;igrave;nh, l&amp;agrave;ng thời trang thế giới nhanh ch&amp;oacute;ng nhận ra điểm chung trong c&amp;aacute;c BST đ&amp;oacute; ch&amp;iacute;nh l&amp;agrave; sự xuất hiện với tần suất v&amp;ocirc; c&amp;ugrave;ng lớn của gam m&amp;agrave;u đỏ.&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/3c678b8f463add3e84e10c66283adc3a_1024x1024.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;M&amp;agrave;u đỏ được nhiều fashionista lựa chọn.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Đặc biệt l&amp;agrave; từ c&amp;aacute;c thương hiệu đang theo đuổi tr&amp;agrave;o lưu &amp;ldquo;See now, buy now&amp;rdquo; như Givenchy, Prada, Fendi, m&amp;agrave;u đỏ được lăng x&amp;ecirc; hết sức nhiệt t&amp;igrave;nh. Lu&amp;ocirc;n bắt kịp c&amp;aacute;c xu hướng mới nhất h&amp;ocirc;m nay Juno kịp c&amp;oacute; rất nhiều mẫu gi&amp;agrave;y t&amp;uacute;i m&amp;agrave;u đỏ đẹp rực rỡ, sẵn s&amp;agrave;ng cho n&amp;agrave;ng tham khảo đ&amp;acirc;y.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/14-givenchy-rtw-fall-2017_1024x1024.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;em&gt;BST Thu - Đ&amp;ocirc;ng 2017&amp;nbsp;Givenchy&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;V&amp;iacute; cầm tay Juno trang tr&amp;iacute; đường chỉ nổi VI049&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;Sở hữu gam m&amp;agrave;u đỏ n&amp;oacute;ng bỏng mẫu v&amp;iacute; Vi049 sẵn s&amp;agrave;ng để mang tới may mắn cho bất kỳ c&amp;ocirc; n&amp;agrave;ng n&amp;agrave;o sở hữu. Đặc biệt đường chỉ trang tr&amp;iacute; nổi bật, họa tiết h&amp;igrave;nh học gi&amp;uacute;p chiếc v&amp;iacute; th&amp;ecirc;m phần c&amp;aacute; t&amp;iacute;nh hơn.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;&amp;nbsp;V&amp;iacute; c&amp;acirc;m tay Juno trang tr&amp;iacute; đường chỉ nổi VI049&quot; href=&quot;https://juno.vn/products/vi-vi049&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;//file.hstatic.net/1000003969/file/do-gam-mau-duoc-cac-nha-mot-dong-loat-lang-xe__14__1024x1024.jpg&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;(Click v&amp;agrave;o để xem sản phẩm tham khảo:&lt;a title=&quot;&amp;nbsp;V&amp;iacute; cầm tay Juno trang tr&amp;iacute; đường chỉ nổi VI049&quot; href=&quot;https://juno.vn/products/vi-vi049&quot; target=&quot;_blank&quot; rel=&quot;noopener noreferrer&quot;&gt;&amp;nbsp;V&amp;iacute; cầm tay Juno trang tr&amp;iacute; đường chỉ nổi VI049&lt;/a&gt;)&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;a title=&quot;&amp;n